    `description`   varchar(2000) DEFAULT NULL COMMENT 'Description',
    `avatar_url`    varchar(1024) DEFAULT NULL COMMENT 'Avatar URL',
    `uid`           varchar(128)  DEFAULT NULL COMMENT 'Creator ID',
